// SPDX-License-Identifier: MIT

pragma solidity ^0.8.20;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "hardhat-deploy/solc_0.8/proxy/Proxied.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {StringUtils} from "@zk-email/contracts/utils/StringUtils.sol";

import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC4626Upgradeable.sol";
import "hardhat-deploy/solc_0.8/proxy/Proxied.sol";
import "hardhat/console.sol";

contract DeRampVault is Initializable, ERC4626Upgradeable, UUPSUpgradeable, OwnableUpgradeable {
	using StringUtils for uint256[];

	// a mapping that checks if a user has deposited the token
	mapping(address => uint256) public shareHolder;
	uint256 public minDepositAmount;
	ERC20 public uasset;

	uint256 public maxOnRampAmount;
	uint256 public sustainabilityFee; // Fee charged to on-rampers in preciseUnits (1e16 = 1%)
	address public sustainabilityFeeRecipient;

	event OffRamp(string payment_id, string payment_platform, uint256 depositAmount, address offramper);

	constructor() {
		_disableInitializers();
	}

	function initialize(
		ERC20 _asset,
		string memory _name,
		string memory _symbol,
		uint256 max,
		uint256 min,
		uint256 fee,
		address benefactor
	) public initializer {
		postUpgrade(_asset, _name, _symbol, max, min, fee, benefactor);
	}

	function postUpgrade(
		ERC20 _asset,
		string memory _name,
		string memory _symbol,
		uint256 max,
		uint256 min,
		uint256 fee,
		address benefactor
	) public {
		uasset = _asset;
		minDepositAmount = min;
		maxOnRampAmount = max;
		sustainabilityFee = fee;
		sustainabilityFeeRecipient = benefactor;

		__ERC20_init(_name, _symbol);
		__ERC4626_init(_asset);
		__Ownable_init(benefactor);

		__UUPSUpgradeable_init();
	}

	function offramp(string memory paymentProcessor, string memory ppId, uint amount, address _receiver) public {
		//	console.log("offramp: %s %s %x %x", paymentProcessor, ppId, amount,_receiver );
		console.log("offramp from %s to %s %s", _receiver, amount, ppId);

		deposit(amount, _receiver);
		emit OffRamp(ppId, paymentProcessor, amount, _receiver);
	}
	/**
	 * @notice function to deposit assets and receive vault tokens in exchange
	 * @param _assets amount of the asset token
	 */
	function deposit(uint _assets, address _receiver) public override returns (uint256) {
		// checks that the deposited amount is greater than zero.
		console.log("deposit from %s to %d tokens: rec: %s", msg.sender, _assets, _receiver);
		require(_assets > 0, "Deposit less than Zero");
		// calling the deposit function from the ERC-4626 library to perform all the necessary functionality
		uint256 shares = super.deposit(_assets, _receiver);
		shareHolder[_receiver] += _assets;
		return shares;
	}

	/**
	 * @notice Function to allow msg.sender to withdraw their deposit plus accrued interest
	 * @param _shares amount of shares the user wants to convert
	 * @param _receiver address of the user who will receive the assets
	 */
	function onramp(uint _shares, address offRamper, address _receiver) public onlyOwner {
		// checks that the deposited amount is greater than zero.
		require(_shares > 0, "withdraw must be greater than Zero");
		// Checks that the _receiver address is not zero.
		require(_receiver != address(0), "Zero Address");
		// checks that the caller is a shareholder
		require(shareHolder[offRamper] > 0, "Not a share holder");
		// checks that the caller has more shares than they are trying to withdraw.
		require(shareHolder[offRamper] >= _shares, "Not enough shares");

		// Calculate 10% yield on the withdrawal amount
		//uint256 percent = (10 * _shares) / 100;
		// Calculate the total asset amount as the sum of the share amount plus 10% of the share amount.
		//uint256 assets = _shares + percent;
		// calling the redeem function from the ERC-4626 library to perform all the necessary functionality
		//redeem(assets, _receiver, offRamper);
		//__Context_init

		//uint256 maxShares = maxRedeem(offRamper);
		//  if (_shares > maxShares) {
		//     revert ERC4626ExceededMaxRedeem(owner, _shares, maxShares);
		// }

		uint256 totalOnramp = previewRedeem(_shares);
		_burn(offRamper, totalOnramp);
		SafeERC20.safeTransfer(uasset, _receiver, _shares);

		//emit Withdraw(offRamper, _receiver, owner, uasset, _shares);

		///
		// Decrease the share of the user
		shareHolder[offRamper] -= _shares;
	}
	/**
	 * @notice Function to allow msg.sender to withdraw their deposit plus accrued interest
	 * @param _shares amount of shares the user wants to convert
	 * @param _receiver address of the user who will receive the assets
	 */
	function withdrawDeposit(uint _shares, address _receiver) public onlyOwner {
		// checks that the deposited amount is greater than zero.
		require(_shares > 0, "withdraw must be greater than Zero");
		// Checks that the _receiver address is not zero.
		require(_receiver != address(0), "Zero Address");
		// checks that the caller is a shareholder
		require(shareHolder[_receiver] > 0, "Not a share holder");
		// checks that the caller has more shares than they are trying to withdraw.
		require(shareHolder[_receiver] >= _shares, "Not enough shares");

		// Calculate 10% yield on the withdrawal amount
		//uint256 percent = (10 * _shares) / 100;
		// Calculate the total asset amount as the sum of the share amount plus 10% of the share amount.
		//uint256 assets = _shares + percent;
		// calling the redeem function from the ERC-4626 library to perform all the necessary functionality
		//redeem(assets, _receiver, offRamper);
		//__Context_init

		//uint256 maxShares = maxRedeem(offRamper);
		//  if (_shares > maxShares) {
		//     revert ERC4626ExceededMaxRedeem(owner, _shares, maxShares);
		// }

		uint256 totalOnramp = previewRedeem(_shares);
		_burn(_receiver, totalOnramp);
		SafeERC20.safeTransfer(uasset, _receiver, _shares);

		//emit Withdraw(offRamper, _receiver, owner, uasset, _shares);

		///
		// Decrease the share of the user
		shareHolder[_receiver] -= _shares;
	}

	function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

	function withdraw(uint256 assets, address receiver, address owner) public override onlyOwner returns (uint256) {
		return super.withdraw(assets, receiver, owner);
	}

	function mint(uint256 shares, address receiver) public override onlyOwner returns (uint256) {
		return super.mint(shares, receiver);
	}
}

src/ERC20/ERC20Base.sol.content:
// SPDX-License-Identifier: AGPL-1.0
pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./ERC20Internal.sol";
import "../Libraries/Constants.sol";
import "hardhat/console.sol";

interface ITransferReceiver {
	function onTokenTransfer(address, uint256, bytes calldata) external returns (bool);
}

interface IPaidForReceiver {
	function onTokenPaidFor(
		address payer,
		address forAddress,
		uint256 amount,
		bytes calldata data
	) external returns (bool);
}

interface IApprovalReceiver {
	function onTokenApproval(address, uint256, bytes calldata) external returns (bool);
}

abstract contract ERC20Base is IERC20, ERC20Internal {
	using Address for address;

	uint256 internal _totalSupply;
	mapping(address => uint256) internal _balances;
	mapping(address => mapping(address => uint256)) internal _allowances;

	function burn(uint256 amount) external virtual {
		address sender = msg.sender;
		_burnFrom(sender, amount);
	}

	function _internal_totalSupply() internal view override returns (uint256) {
		return _totalSupply;
	}

	function totalSupply() public view override returns (uint256) {
		return _internal_totalSupply();
	}

	function balanceOf(address owner) external view override returns (uint256) {
		return _balances[owner];
	}

	function allowance(address owner, address spender) external view override returns (uint256) {
		if (owner == address(this)) {
			// see transferFrom: address(this) allows anyone
			return Constants.UINT256_MAX;
		}
		return _allowances[owner][spender];
	}

	function decimals() external pure virtual returns (uint8) {
		return uint8(18);
	}

	function transfer(address to, uint256 amount) external override returns (bool) {
		_transfer(msg.sender, to, amount);
		return true;
	}

	function transferAlongWithETH(address payable to, uint256 amount) external payable returns (bool) {
		_transfer(msg.sender, to, amount);
		to.transfer(msg.value);
		return true;
	}

	function distributeAlongWithETH(address payable[] memory tos, uint256 totalAmount) external payable returns (bool) {
		uint256 val = msg.value / tos.length;
		require(msg.value == val * tos.length, "INVALID_MSG_VALUE");
		uint256 amount = totalAmount / tos.length;
		require(totalAmount == amount * tos.length, "INVALID_TOTAL_AMOUNT");
		for (uint256 i = 0; i < tos.length; i++) {
			_transfer(msg.sender, tos[i], amount);
			tos[i].transfer(val);
		}
		return true;
	}

	function transferAndCall(address to, uint256 amount, bytes calldata data) external returns (bool) {
		_transfer(msg.sender, to, amount);
		return ITransferReceiver(to).onTokenTransfer(msg.sender, amount, data);
	}

	function transferFromAndCall(
		address from,
		address to,
		uint256 amount,
		bytes calldata data
	) external returns (bool) {
		_transferFrom(from, to, amount);
		return ITransferReceiver(to).onTokenTransfer(from, amount, data);
	}

	function payForAndCall(
		address forAddress,
		address to,
		uint256 amount,
		bytes calldata data
	) external returns (bool) {
		_transfer(msg.sender, to, amount);
		return IPaidForReceiver(to).onTokenPaidFor(msg.sender, forAddress, amount, data);
	}

	function transferFrom(address from, address to, uint256 amount) external override returns (bool) {
		console.log("transfer %s %s %s", from, to, amount);
		_transferFrom(from, to, amount);
		return true;
	}

	function approve(address spender, uint256 amount) external override returns (bool) {
		_approveFor(msg.sender, spender, amount);
		return true;
	}

	function approveAndCall(address spender, uint256 amount, bytes calldata data) external returns (bool) {
		_approveFor(msg.sender, spender, amount);
		return IApprovalReceiver(spender).onTokenApproval(msg.sender, amount, data);
	}

	function _approveFor(address owner, address spender, uint256 amount) internal override {
		require(owner != address(0) && spender != address(0), "INVALID_ZERO_ADDRESS");
		_allowances[owner][spender] = amount;
		emit Approval(owner, spender, amount);
	}

	function _transferFrom(address from, address to, uint256 amount) internal {
		// anybody can transfer from this
		// this allow mintAndApprovedCall without gas overhead
		if (msg.sender != from && from != address(this)) {
			uint256 currentAllowance = _allowances[from][msg.sender];
			if (currentAllowance != Constants.UINT256_MAX) {
				// save gas when allowance is maximal by not reducing it (see https://github.com/ethereum/EIPs/issues/717)
				require(currentAllowance >= amount, "NOT_AUTHOIZED_ALLOWANCE");
				_allowances[from][msg.sender] = currentAllowance - amount;
			}
		}
		_transfer(from, to, amount);
	}

	function _transfer(address from, address to, uint256 amount) internal {
		require(to != address(0), "INVALID_ZERO_ADDRESS");
		require(to != address(this), "INVALID_THIS_ADDRESS");
		uint256 currentBalance = _balances[from];
		require(currentBalance >= amount, "NOT_ENOUGH_TOKENS");
		_balances[from] = currentBalance - amount;
		_balances[to] += amount;
		emit Transfer(from, to, amount);
	}

	function _transferAllIfAny(address from, address to) internal {
		uint256 balanceLeft = _balances[from];
		if (balanceLeft > 0) {
			_balances[from] = 0;
			_balances[to] += balanceLeft;
			emit Transfer(from, to, balanceLeft);
		}
	}

	function _mint(address to, uint256 amount) internal override {
		_totalSupply += amount;
		_balances[to] += amount;
		emit Transfer(address(0), to, amount);
	}

	function _burnFrom(address from, uint256 amount) internal override {
		uint256 currentBalance = _balances[from];
		require(currentBalance >= amount, "NOT_ENOUGH_TOKENS");
		_balances[from] = currentBalance - amount;
		_totalSupply -= amount;
		emit Transfer(from, address(0), amount);
	}
}

